import React, { Component } from "react";
import * as Survey from "survey-react";
import "survey-react/survey.css";
import { createSurvey, createTracking, createFirstLogin } from "../graphql/mutations";
import { listTrackings } from '../graphql/queries';
import { graphqlOperation, API } from "aws-amplify";
import { history } from '../AppWithAuth';
import firstSurvey from '../survey/FirstServey';
import finalSurvey from '../survey/FinalSurvey';

Survey.StylesManager.applyTheme("orange");

class SurveyComponent extends Component {

  state = {
    isCompleted : false,
    finalResult : ""
  }

  onCompleteComponent = async (result) => {
    try {
      if (this.props.hotelLocation === ""){
        console.log(result);
        console.log("Hotel Location :  " + result.data.HotelLocation);
        console.log(this.props.userAttributes);
        await API.graphql(graphqlOperation(createFirstLogin, {input:{'Email':this.props.userAttributes.email, 'Sub':this.props.userAttributes.sub,'LocationHotel':result.data.HotelLocation}}));
      }

      const trakings = await API.graphql(graphqlOperation(listTrackings,  {
          filter: {
              and : [{
              Sub: {
                  eq: this.props.userAttributes.sub
              },
              Week:{
                eq : 'W1'
              }
            }]
          }
        }));

      if (trakings.data.listTrackings.items.length === 0) {
        //Create a first line for tracking
        const data = result.data;
        console.log(data);
        console.log(result.data['RoomsNightsSplit-total'].NbRoomNights);
        console.log(result.data['RoomsNightsSplit-total'].NbRoomNights);
        //result.data['SplitTotalRoomsBusinessSegment-total'].NbRoomsNightsBS;
        //Page 2

        //Page 3
        var TotalNumberRoomAvailable = result.data.TotalNumberRoomAvailable;
        var  NbOccupiedRoomNightsAvril= result.data['SplitTotalRoomAvailable']['NbOfOccupiedRooms'].M1;
        var  NbOccupiedRoomNightsMay= result.data.SplitTotalRoomAvailable.NbOfOccupiedRooms.M2;
        var  NbOccupiedRoomNightsJune= result.data.SplitTotalRoomAvailable.NbOfOccupiedRooms.M3;
        var  NbOccupiedRoomNightsJuly= result.data.SplitTotalRoomAvailable.NbOfOccupiedRooms.M4;
        var  NbOccupiedRoomNightsAugust= result.data.SplitTotalRoomAvailable.NbOfOccupiedRooms.M5;
        var  NbOccupiedRoomNightsSeptember= result.data.SplitTotalRoomAvailable.NbOfOccupiedRooms.M6;
        var  NbTotalRoomNightsAvril= result.data.TotalRoomNightAvailable.NbOfOccupiedRooms.M1;
        var  NbTotalRoomNightsMay= result.data.TotalRoomNightAvailable.NbOfOccupiedRooms.M2;
        var  NbTotalRoomNightsJune= result.data.TotalRoomNightAvailable.NbOfOccupiedRooms.M3;
        var  NbTotalRoomNightsJuly= result.data.TotalRoomNightAvailable.NbOfOccupiedRooms.M4;
        var  NbTotalRoomNightsAugust= result.data.TotalRoomNightAvailable.NbOfOccupiedRooms.M5;
        var  NbTotalRoomNightsSeptember= result.data.TotalRoomNightAvailable.NbOfOccupiedRooms.M6;
        var OccupancyRateApril = result.data['SplitTotalRoomAvailable-total'].M1;
        var OccupancyRateMay =result.data['SplitTotalRoomAvailable-total'].M2;
        var OccupancyRateJune =result.data['SplitTotalRoomAvailable-total'].M3;
        var OccupancyRateJuly =result.data['SplitTotalRoomAvailable-total'].M4;
        var OccupancyRateAugust =result.data['SplitTotalRoomAvailable-total'].M5;
        var OccupancyRateSeptember =result.data['SplitTotalRoomAvailable-total'].M6;

        //await API.graphql(graphqlOperation(createTracking, {input:{'Email':this.props.userAttributes.email, 'Sub':this.props.userAttributes.sub,'Week':'W1'}}));
        //await API.graphql(graphqlOperation(createSurvey, {input:{ TotalNumberRoomAvailable, NbOccupiedRoomNightsAvril  } }));

        this.setState({ isCompleted: true});
        history.push('/Final');
      } else {
        history.push('/FinalRetry');
      }



    } catch (err) {
      console.error("error during delete", err);
    }
  }

  render() {



    var json = (this.props.hotelLocation === "")? firstSurvey : finalSurvey;


    var surveyRender = !this.state.isCompleted ? (
      <Survey.Survey
        json={json}
        //data={data}
        showCompletedPage={false}
        onComplete={this.onCompleteComponent}
      />
    ) : null;


    /*var onCompleteComponent = this.state.isCompleted ? (
    ) : null;*/

    return (
      <div>
        {surveyRender}
      </div>
    );
  }
}

export default SurveyComponent;
