import React from "react";
import * as Survey from "survey-react";
import "survey-react/survey.css";

class SurveyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = { isCompleted: false };
    this.onCompleteComponent = this.onCompleteComponent.bind(this);
  }
  onCompleteComponent() {
    this.setState({ isCompleted: true });
  }
  render() {
    let json = {
      pages: [
        {
          name: "Fermeture de l'hôtel",
          elements: [
            {
              type: "boolean",
              name: "HotelClosed",
              title: "L'hôtel est-il totalement fermé ?",
              isRequired: true,
              labelTrue: "Yes",
              labelFalse: "No"
            },
            {
              type: "text",
              name: "HotelReopenDate",
              visibleIf: "{HotelClosed} = true",
              title: "Si oui, date de réouverture prévue",
              inputType: "date"
            }
          ],
          title: "Fermeture de l'hôtel"
        },
        {
          name: "Confinement",
          elements: [
            {
              type: "boolean",
              name: "ConfinementZone",
              title:
                "Votre zone géographique fait-elle l'objet d'un confinement ?",
              isRequired: true,
              labelTrue: "Yes",
              labelFalse: "No"
            },
            {
              type: "text",
              name: "ConfinementEnd",
              visibleIf: "{ConfinementZone} = true",
              title: "Si oui, date de levée des confinements prévue",
              inputType: "date"
            }
          ],
          title: "Confinement"
        },
        {
          name: "Nb de réservations au cours des 7 derniers jours",
          elements: [
            {
              type: "text",
              name: "NbBookings",
              title: "Nb de réservations au cours des 7 derniers jours",
              inputType: "number"
            },
            {
              type: "text",
              name: "NbCancelation",
              title: "Nb d'annulations au cours des 7 derniers jours",
              inputType: "number"
            }
          ],
          title: "Nb de réservations au cours des 7 derniers jours"
        },
        {
          name:
            "Nationalité des clients ayant réservé au cours des 7 derniers jours ?",
          elements: [
            {
              type: "matrixdropdown",
              name: "ClientLocation",
              title:
                "Nationalité des clients ayant réservé au cours des 7 derniers jours ?",
              columns: [
                {
                  name: "Nom",
                  cellType: "dropdown"
                },
                {
                  name: "Nombre",
                  cellType: "text"
                }
              ],
              choices: [
                {
                  value: "Paris",
                  text: "Paris"
                },
                {
                  value: "Londres",
                  text: "Londres"
                },
                {
                  value: "Maroc",
                  text: "Maroc"
                },
                {
                  value: "Suisse",
                  text: "Suisse"
                },
                {
                  value: "New-York",
                  text: "New-York"
                }
              ],
              rows: ["Pays #1", "Pays #2", "Pays #3", "Autres"]
            }
          ],
          title:
            "Nationalité des clients ayant réservé au cours des 7 derniers jours ?"
        },
        {
          name: "Quel business segment a réservé au cours des 7 de",
          elements: [
            {
              type: "multipletext",
              name: "BusinessSegment",
              title:
                "Quel business segment a réservé au cours des 7 derniers jours",
              items: [
                {
                  name: "FIT",
                  title: "FIT"
                },
                {
                  name: "MICE",
                  title: "MICE"
                },
                {
                  name: "Corporate",
                  title: "Corporate"
                }
              ]
            }
          ],
          title: "Quel business segment a réservé au cours des 7 de"
        }
      ]
    };
    var surveyRender = !this.state.isCompleted ? (
      <Survey.Survey
        json={json}
        showCompletedPage={false}
        onComplete={this.onCompleteComponent}
      />
    ) : null;
    var onCompleteComponent = this.state.isCompleted ? (
      <div>The component after onComplete event</div>
    ) : null;
    return (
      <div>
        {surveyRender}
        {onCompleteComponent}
      </div>
    );
  }
}

export default SurveyComponent;
