import React from "react";
import {Auth, Hub}  from 'aws-amplify'
import App from "./App";
import Navbar from './components/NavBar'
import Logo from './images/GL-Group-BLACK-RVB.jpg';
import { Authenticator } from "aws-amplify-react";
import createBrowserHistory from 'history/createBrowserHistory';
import { Router, Route} from 'react-router-dom';
import HomePage from './pages/HomePage';
import FinalPage from './pages/FinalPage';

export const UserContext = React.createContext();

export const history = createBrowserHistory();

class AppWithAuth extends React.Component {
  state = {
      user : null
  }

  componentDidMount() {
    this.getUserData();
    Hub.listen('auth', this, 'onHubCapsule');
  }

  getUserData = async () => { 
    const user = await  Auth.currentAuthenticatedUser();
    user ? this.setState({user}, () => this.getUserAttributes(this.state.user)) : this.setState({user : null});
  }

  getUserAttributes = async authUserData  => {
    const attributesArr = Auth.userAttributes(authUserData).then(data => {
      const attributesObj = Auth.attributesToObject(data);
      this.setState({ userAttributes : attributesObj });
    });

  }

  handleSignOut = async () =>{
    try {
      await Auth.signOut()
    } catch(err){
      console.error('Error signing out the user', err);
    }
  }

  onHubCapsule = capsule => {
    switch (capsule.payload.event) {
      case "signIn" :
        console.log('signed in');
        this.getUserData();
        break;
      case "signUp":
          console.log('signed up');
        break;
      case "signOut":
        console.log('signed out');
        this.setState({user: null});
        break;
      default : 
        return;
    }
  }

  render() {
    const { user } = this.state;
    return (
        !user ?  (
            <>
            <div style={{alignContent:'center'}}>
                <img src={Logo} alt="GLH Logo" style={{width:'250px',display: 'block', margin: 'auto', marginTop:'30px'}}></img>
            </div>

            <Authenticator/>
            </>
          ) : (
            <UserContext.Provider value={{ user }}>
                <Router history={history}> 
                    <React.Fragment>
                        <Navbar user={user} handleSignOut={this.handleSignOut}/>
                        <div style={{marginTop:'30px'}}/>
                        <App />
                    </React.Fragment>
                    <Route exact path="/" component ={HomePage} />
                    <Route exact path="/Final" component ={FinalPage} />
                </Router>
            </UserContext.Provider>)

    );
  }
}

export default AppWithAuth;
