        var  NbRoomNightsBookedCorporate = result.data['SplitTotalRoomsBusinessSegment']['Corporate'].NbRoomsNightsBS;
        var  RemainingRoomNightsBySegment = result.data['SplitTotalRoomsBusinessSegment-total'].NbRoomsNightsBS;

        var  RoomNightsCountry1 = result.data['RoomsNightsSplit']['Country1'].Country;
        var  NbRoomNightsBookedCountry1 = result.data['RoomsNightsSplit']['Country1'].NbRoomNights;
        console.log(RoomNightsCountry1);
