        var  RoomNightsCountry4 = result.data['RoomsNightsSplit']['Country4'].Country;
        var  NbRoomNightsBookedCountry4 = result.data['RoomsNightsSplit']['Country4'].NbRoomNights;
        var  RoomNightsCountry5 = result.data['RoomsNightsSplit']['Country5'].Country;
        var  NbRoomNightsBookedCountry5 = result.data['RoomsNightsSplit']['Country5'].NbRoomNights;*/

        var  NbRoomNightsBookedLastWeekApril = result.data['SplitByMonth']['NbRoomsByMonth'].M1;
        var  NbRoomNightsBookedLastWeekMay = result.data['SplitByMonth']['NbRoomsByMonth'].M2;
        var  NbRoomNightsBookedLastWeekJune = result.data['SplitByMonth']['NbRoomsByMonth'].M3;
        var  NbRoomNightsBookedLastWeekJuly = result.data['SplitByMonth']['NbRoomsByMonth'].M4;
        var  NbRoomNightsBookedLastWeekAugust = result.data['SplitByMonth']['NbRoomsByMonth'].M5;
        var  NbRoomNightsBookedLastWeekSeptember = result.data['SplitByMonth']['NbRoomsByMonth'].M6;
        var  TotalNightsBookedLastWeek = result.data['SplitByMonth']['NbRoomsByMonth'].M7;

        //Page 3
        var TotalNumberRoomAvailable = result.data.TotalNumberRoomAvailable;
        var  NbOccupiedRoomNightsAvril= result.data['SplitTotalRoomAvailable']['NbOfOccupiedRooms'].M1;
        var  NbOccupiedRoomNightsMay=  result.data['SplitTotalRoomAvailable']['NbOfOccupiedRooms'].M2;
        var  NbOccupiedRoomNightsJune=  result.data['SplitTotalRoomAvailable']['NbOfOccupiedRooms'].M3;
        var  NbOccupiedRoomNightsJuly=  result.data['SplitTotalRoomAvailable']['NbOfOccupiedRooms'].M4;
        var  NbOccupiedRoomNightsAugust=  result.data['SplitTotalRoomAvailable']['NbOfOccupiedRooms'].M5;
        var  NbOccupiedRoomNightsSeptember=  result.data['SplitTotalRoomAvailable']['NbOfOccupiedRooms'].M6;
        var  NbTotalRoomNightsAvril=  result.data['SplitTotalRoomAvailable']['TotalRoomNightAvailable'].M1;
        var  NbTotalRoomNightsMay=  result.data['SplitTotalRoomAvailable']['TotalRoomNightAvailable'].M2;
        var  NbTotalRoomNightsJune=  result.data['SplitTotalRoomAvailable']['TotalRoomNightAvailable'].M3;
