        var  NbTotalRoomNightsAugust=  result.data['SplitTotalRoomAvailable']['TotalRoomNightAvailable'].M5;
        var  NbTotalRoomNightsSeptember=  result.data['SplitTotalRoomAvailable']['TotalRoomNightAvailable'].M6;
        var OccupancyRateApril = result.data['SplitTotalRoomAvailable-total'].M1;
